var Repo = require('../models/repo')
  , Base = require('./base');

module.exports = Base.extend({
  model: Repo,
  url: function() {
    if (this.params.user != null) {
      return '/users/user_name/repos.json'; //'/users/:user/repos.json';
    } else {
      return '/repositories.json';
    }
  }
});
module.exports.id = 'Repos';
